import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';
import MainPart from './MainPart';
import Register from './Register';
import Login from './Login';
import Profile from './Profile';

function App() {


  const [activeComponent, setActiveComponent] = useState('main');

    const handleLoginClick = () => {
        setActiveComponent('login');
    };

    const handleRegisterClick = () => {
        setActiveComponent('register');
    };

    const handleProfile = () => {
      setActiveComponent('profile');
  };

    const handleMainPart = () => {
      setActiveComponent('main');
  };

    const renderComponent = () => {
        switch (activeComponent) {
            case 'login':
                return <Login />;
            case 'register':
                return <Register />;
            case 'profile':
              return <Profile />;
            case 'main':
            default:
                return <MainPart currency={selectedCurrency}/>;
        }
  };

  // Currency thing

  const [selectedCurrency, setSelectedCurrency] = useState('USD');

  // List of currencies
  const currencies = [
    { code: 'USD', name: 'United States Dollar', symbol: '$' },
    { code: 'EUR', name: 'Euro', symbol: '€' },
    { code: 'GBP', name: 'British Pound Sterling', symbol: '£' },
    { code: 'JPY', name: 'Japanese Yen', symbol: '¥' },
    { code: 'AUD', name: 'Australian Dollar', symbol: 'A$' },
    { code: 'CAD', name: 'Canadian Dollar', symbol: 'C$' },
    { code: 'PKR', name: 'Pakistani Rupees', symbol: '₨' },
  ];

  const handleCurrencyChange = (e) => {
    const selectedSymbol = e.target.value;
    setSelectedCurrency(selectedSymbol);
  };

  return (
    <div className="App">
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="title container-fluid">
          <h3 onClick={handleMainPart} className="navbar-brand">Receiptify</h3>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link" onClick={handleMainPart}>Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" onClick={handleLoginClick}>Login</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" onClick={handleRegisterClick}>Register</a>
              </li>
            </ul>
            <div className="d-flex align-items-center">
              <select
                value={selectedCurrency}
                onChange={handleCurrencyChange}
                className="form-select me-2"
              >
                {currencies.map((currency) => (
                  <option key={currency.code} value={currency.code}>
                    ({currency.code})
                  </option>
                ))}
              </select>
              <div className="p-2 profile-icon">
                <i onClick={handleProfile} className="fa fa-user"></i>
              </div>
            </div>
          </div>
        </div>
    </nav>
      <div className="bg-color p-3 pt-5">
        <div className='max-content-width'>
          {renderComponent()}
        </div>
      </div>
    </div>
  );
}

export default App;
