import React, { useState, useEffect} from 'react';
import barcode from './barcode.png';
import html2canvas from 'html2canvas';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function MainPart({currency}) {
    const [rows, setRows] = useState([]); // Define rows state here

    const addRow = () => {
        setRows([...rows, { name: '', quantity: 1, price: '' }]);
    };

    const handleInputChange = (index, event) => {
        const { name, value } = event.target;
        const newRows = [...rows];
        newRows[index][name] = value;
        setRows(newRows);
    };

    const calculateTotalAmount = () => {
        return rows.reduce((total, row) => {
            return total + (parseFloat(row.price) * parseFloat(row.quantity) || 0);
        }, 0);
    };

    const [companyName, setCompanyName] = useState('');
    const [slogan, setSlogan] = useState('');
    const [customerName, setCustomerName] = useState('');
    const [tax, setTax] = useState('');

    const subtotal = calculateTotalAmount();
    const taxAmount = (subtotal * tax) / 100;
    const netPayable = subtotal + taxAmount;

    const handleCompanyNameChange = (event) => {
        setCompanyName(event.target.value);
    };

    const handleSloganChange = (event) => {
        setSlogan(event.target.value);
    };

    const handleCustomerNameChange = (event) => {
        setCustomerName(event.target.value);
    };

    const handleTaxChange = (event) => {
        setTax(event.target.value);
    };

    const [currentDate, setCurrentDate] = useState(new Date());

    // Update the date every second
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentDate(new Date());
        }, 1000);

        // Clear the interval on component unmount
        return () => clearInterval(timer);
    }, []);

    const formattedDate = currentDate.toLocaleDateString();

    // Download and upload

    const [file, setFile] = useState(null);
    const [uploadUrl, setUploadUrl] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleDownload = async () => {
        const element = document.getElementById('the-receipt');
        if (element) {
          html2canvas(element).then(async (canvas) => {
            // Create an image URL from the canvas
            const imgData = canvas.toDataURL('image/png');

            // Create a link element
            const link = document.createElement('a');
            link.href = imgData;
            link.download = 'receipt.png';

            // Append the link to the body (required for Firefox)
            document.body.appendChild(link);

            // Trigger the download
            link.click();

            // Remove the link from the body
            document.body.removeChild(link);

            const response = await fetch(imgData);
            const blob = await response.blob();
            const file = new File([blob], 'receipt.png', { type: 'image/png' });

            // Set the file state
            setFile(file);

            // Upload the file
            handleUpload(file);
          });
        }
      };

    const handleUpload = async (file) => {
        if (!file) {
        alert('Please select a file first.');
        return;
        }

        const formData = new FormData();
        formData.append('file', file);

        try {
        const response = await axios.post('http://localhost:8000/upload', formData, {
            headers: {
            'Content-Type': 'multipart/form-data'
            }
        });

        if (response.status === 200) {
            console.log('File uploaded successfully:', response.data.url);
            setUploadUrl(response.data.url);
        } else {
            console.error('Failed to upload file');
        }
        } catch (error) {
        console.error('Error uploading file:', error);
        }
    };


    // Fetching company name and slogan

    const [userData, setUserData] = useState({
        companyName: '',
        companySlogan: '',
      });

      // Getting data

      useEffect(() => {
        const fetchUserData = async () => {
            try {
                const token = localStorage.getItem('authToken'); // Retrieve token from localStorage

                if (!token) {
                    console.error('No token found');
                    toast.error('Please log in to continue.');
                    return;
                }

                const response = await axios.get('http://localhost:8000/api/user', {
                    headers: {
                        Authorization: `Bearer ${token}` // Include token in Authorization header
                    }
                });

                console.log('User data response:', response.data);

                setUserData(response.data);
            } catch (error) {
                if (error.response && error.response.status === 401) {
                    console.error('Token is invalid or expired:', error);
                    toast.error('Session expired. Please log in again.');
                    localStorage.removeItem('authToken');
                    window.location.href = '/login';
                } else if (error.response && error.response.status === 404) {
                    console.error('Route not found:', error);
                    toast.error('The requested resource was not found.');
                } else {
                    console.error('Error fetching user data:', error);
                }
            }
        };

        fetchUserData();
      }, []); 

    return (
        <div className="main-part row d-flex justify-content-around">
            <ToastContainer />
            <div className="col-12 col-sm-12 col-md-7 col-lg-7 pb-3">
                <div className="inner-bg p-3 py-4">
                    <h4 className="h4 pb-3">
                        Enter Receipt Details
                    </h4>
                    <div className='receipt-inputs'>
                        <div className='d-flex justify-content-between row pb-3'>
                            <div className="col">
                                <p className="pb-1 m-0 profile-text">Company name</p>
                                <input
                                    className='py-2 px-2 form-control'
                                    type="text"
                                    placeholder="Company Name"
                                    name="companyName"
                                    value={userData.companyName || companyName}
                                    onChange={handleCompanyNameChange}
                                />
                            </div>
                            <div className="col">
                                <p className="pb-1 m-0 profile-text">Slogan</p>
                                <input
                                    className='py-2 px-2 form-control'
                                    type="text"
                                    placeholder="Slogan (Optional)"
                                    name="slogan"
                                    value={userData.companySlogan || slogan}
                                    onChange={handleSloganChange}
                                />
                            </div>

                        </div>
                        <div className='d-flex row justify-content-between'>
                            <div className="col">
                                <p className="pb-1 m-0 profile-text">Customer name</p>
                                <input
                                    className='py-2 px-2 form-control'
                                    type="text"
                                    placeholder="Customer Name"
                                    name="customerName"
                                    value={customerName}
                                    onChange={handleCustomerNameChange}
                                />
                            </div>
                            <div className="col">
                                <p className="pb-1 m-0 profile-text">Tax</p>
                                <input
                                    className='py-2 px-2 form-control'
                                    type="number"
                                    placeholder="Tax (%)"
                                    name="tax"
                                    value={tax}
                                    onChange={handleTaxChange}
                                />
                            </div>


                        </div>

                        <div className='receipt-items my-3 pt-3'>
                            {rows.map((row, index) => (
                                <div className="row items-row mb-3" key={index}>
                                    <div className="col p-0">
                                        <input
                                            type="text"
                                            className="form-control"
                                            placeholder="Item Name"
                                            name="name"
                                            value={row.name}
                                            onChange={(event) => handleInputChange(index, event)}
                                        />
                                    </div>
                                    <div className="col p-0">
                                        <input
                                            type="number"
                                            className="form-control"
                                            placeholder="Quantity"
                                            name="quantity"
                                            value={row.quantity}
                                            onChange={(event) => handleInputChange(index, event)}
                                        />
                                    </div>
                                    <div className="col p-0">
                                        <input
                                            type="number"
                                            className="form-control"
                                            placeholder="Price"
                                            name="price"
                                            value={row.price}
                                            onChange={(event) => handleInputChange(index, event)}
                                        />
                                    </div>
                                </div>
                            ))}

                        </div>
                        <button type="button" onClick={addRow} className="mx-1 add-item-btn my-1 p-2 px-3">Add Item</button>
                    </div>
                </div>
            </div>
            <div className="col-12 col-sm-12 col-md-4 col-lg-4">
                <div className="inner-bg p-3 py-4">
                    <h4 className="h4 pb-2">
                        Your Receipt
                    </h4>
                    <div className='receipt-adj px-3 mb-3'>
                        <div id='the-receipt' className='the-receipt px-4 pt-5 pb-4'>
                            <div className='receipt-content my-4 px-2'>
                                <h5 className='text-center r-company'>{userData.companyName || companyName}</h5>
                                <h6 className='text-center r-slogan'>{userData.companySlogan || slogan}</h6>
                                <p className='r-p py-2 customer-para'>Customer name: {customerName}</p>
                                <div className='dotted-line pb-2 px-2'>
                                </div>
                                <div className='receipt-items-attributes row px-3'>
                                    <div className="col p-0">
                                        <p>Item</p>
                                    </div>
                                    <div className="col p-0">
                                        <p>Qty.</p>
                                    </div>
                                    <div className="col p-0">
                                        <p>Price</p>
                                    </div>
                                </div>
                                <div className='dotted-line pb-2 px-2'></div>
                                <div className='receipt-items px-3 my-3'>
                                    {rows.map((row, index) => (
                                        <div key={index} className="row items-row mb-1">
                                            <div className="col p-0">
                                                <p>{row.name}</p>
                                            </div>
                                            <div className="col p-0">
                                                <p>{row.quantity}</p>
                                            </div>
                                            <div className="col p-0">
                                                <p>{row.price}</p>
                                            </div>
                                        </div>
                                    ))}
                                </div>  
                                <div className='dotted-line pb-2 px-2'></div>
                                <div className='r-calculations row px-3'>
                                    <div className='col p-0'>

                                    </div>
                                    <div className='col p-0'>
                                        <p className='r-subtotal'>Subtotal:</p>
                                    </div>
                                    <div className='col p-0'>
                                        <p className='r-subtotal'>{currency} {subtotal.toFixed(2)}</p>
                                    </div>
                                </div>
                                <div className='r-calculations row px-3'>
                                    <div className='col p-0'>

                                    </div>
                                    <div className='col p-0'>
                                        <p className='r-subtotal'>Tax:</p>
                                    </div>
                                    <div className='col p-0'>
                                        <p className='r-subtotal'>{currency} {taxAmount.toFixed(2)} ({tax}%)</p>
                                    </div>
                                </div>
                                <div className='dotted-line pb-2 px-2'>
                                </div>
                                <div className='r-calculations row px-3'>
                                    <div className='col p-0'>

                                    </div>
                                    <div className='col p-0'>
                                        <p className='r-subtotal'>Net payable:</p>
                                    </div>
                                    <div className='col p-0'>
                                        <p className='r-subtotal'>{currency} {netPayable.toFixed(2)}</p>
                                    </div>
                                </div>
                                <div className='barcode py-3'>
                                    <img className="barcode-img img-fluid" src={barcode}/>
                                </div>
                                <div className='d-flex row justify-content-between pt-2 px-1'>
                                    <div className='col'>
                                        <p className='r-para'>{formattedDate}</p>
                                    </div>

                                    <div className='col text-end'>
                                        <p className='r-para'>{currentDate.toLocaleTimeString()}</p>
                                    </div>

                                </div>
                                <div className='pt-2 px-2'>
                                    <p className='r-thank-para text-center'>Thank You</p>    
                                </div>
                                <div className='pt-2 px-1'>
                                    <p className='r-big-para m-0'>Powered by: Receptify</p>
                                    <p className='r-big-para m-0'>App by: Abdul Wahab</p>    
                                </div>
                            </div>

                        </div>

                    </div>
                    <button onClick={handleDownload} type="button" className="mx-1 add-item-btn my-1 p-2 px-3">Download Receipt</button>
                    {/* {uploadUrl && (
                        <div>
                        <p>Image uploaded successfully:</p>
                        <a href={uploadUrl} target="_blank" rel="noopener noreferrer">View Image</a>
                        </div>
                    )} */}
                </div>
            </div>
        </div>
    );
}

export default MainPart;
