import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';

const LogoUpload = ({ onLogoChange }) => {
    const [logo, setLogo] = useState(null);

    const handleLogoChange = (acceptedFiles) => {
        const file = acceptedFiles[0];
        const reader = new FileReader();

        reader.onload = () => {
            const logoUrl = reader.result;
            setLogo(logoUrl);
            onLogoChange(logoUrl); // Pass the logo URL to the parent component
        };

        reader.readAsDataURL(file);
    };

    const { getRootProps, getInputProps } = useDropzone({
        onDrop: handleLogoChange,
        accept: 'image/*',
    });

    return (
        <div className='d-flex align-items-center'>
            <div className='logo-upload-div' {...getRootProps()} style={{ border: '2px dashed #cccccc', padding: '12px', textAlign: 'center' }}>
                <input {...getInputProps()} />
                <p className='m-0'>Drag 'n' drop a logo here, or click to select a file</p>
            </div>
            {logo && (
                <div className='pb-4'>
                    <img src={logo} alt="Company Logo" style={{ width: '50px', height: '50px', marginTop: '20px' }} />
                </div>
            )}
        </div>
    );
};

export default LogoUpload;
