import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';
import MainPart from './MainPart';
import Register from './Register';
import Login from './Login';
import Profile from './Login';

function App() {
  const [activeComponent, setActiveComponent] = useState('main');

    const handleLoginClick = () => {
        setActiveComponent('login');
    };

    const handleRegisterClick = () => {
        setActiveComponent('register');
    };

    const handleMainPart = () => {
      setActiveComponent('main');
  };

    const renderComponent = () => {
        switch (activeComponent) {
            case 'login':
                return <Login />;
            case 'register':
                return <Register />;
            case 'profile':
              return <Profile />;
            case 'main':
            default:
                return <MainPart />;
        }
  };


  return (
    <div className="App">
      <div className='title d-flex justify-content-center'>
        <div className="d-flex justify-content-between align-items-center max-content-width px-4 mx-1">
          <div>
            <h3 className="app-title text-center py-2 pb-3 px-1">Receiptify</h3>
          </div>
          <div className='d-flex'>
              <button
                  type="button"
                  className="mx-1 login-register-btn my-1 p-1 px-3"
                  onClick={handleMainPart}
              >
                Home
              </button>
              <button
                type="button"
                className="mx-1 login-register-btn my-1 p-1 px-3"
                onClick={handleLoginClick}
              >
                  Login
              </button>
              <button
                  type="button"
                  className="mx-1 login-register-btn my-1 p-1 px-3"
                  onClick={handleRegisterClick}
              >
                  Register
            </button>
            <div className='p-2 profile-icon'>
              <i className="pt-1 fa-solid fa-user"></i> 
            </div>

          </div>
        </div>


      </div>
      <div className="bg-color p-3 pt-5">
        <div className='max-content-width'>
          {renderComponent()}
        </div>
      </div>
    </div>
  );
}

export default App;
