import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Profile() {
    const [userData, setUserData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        companyName: '',
        companySlogan: '',
    });

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const token = localStorage.getItem('authToken'); // Retrieve token from localStorage

                if (!token) {
                    console.error('No token found');
                    toast.error('Please log in to continue.');
                    return;
                }

                const response = await axios.get('http://localhost:8000/api/user', {
                    headers: {
                        Authorization: `Bearer ${token}` // Include token in Authorization header
                    }
                });

                console.log('User data response:', response.data);

                setUserData(response.data);
            } catch (error) {
                if (error.response && error.response.status === 401) {
                    console.error('Token is invalid or expired:', error);
                    toast.error('Session expired. Please log in again.');
                    localStorage.removeItem('authToken');
                    window.location.href = '/login';
                } else if (error.response && error.response.status === 404) {
                    console.error('Route not found:', error);
                    toast.error('The requested resource was not found.');
                } else {
                    console.error('Error fetching user data:', error);
                    toast.error('There was an error fetching user data.');
                }
            }
        };

        fetchUserData();
    }, []);

    const handleChange = (e) => {
        setUserData({
            ...userData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('authToken'); // Retrieve token from localStorage

            if (!token) {
                console.error('No token found');
                toast.error('Please log in to continue.');
                return;
            }

            await axios.put('http://localhost:8000/api/user', userData, {
                headers: {
                    Authorization: `Bearer ${token}` // Include token in Authorization header
                }
            });

            toast.success('Your account details have been updated successfully!');
        } catch (error) {
            toast.error('There was an error updating your account details!');
            console.error('Error updating user data:', error);
        }
    };

    return (
        <div className="row d-flex justify-content-around">
            <ToastContainer />
            <div className="col-12 col-sm-12 col-md-7 col-lg-7 pb-3">
                <div className="inner-bg p-3 py-4">
                    <h4 className="h4">Your Account Details</h4>
                    <div>
                        {userData ? (
                            <div>
                                <h1>Welcome, {userData.email}</h1>
                                {/* Render additional user data here */}
                            </div>
                        ) : (
                            <p>Loading...</p>
                        )}
                    </div>
                    <form className='receipt-inputs' onSubmit={handleSubmit}>
                        <div className='py-1'>
                            <p className="pb-1 m-0 profile-text">First name</p>
                            <input
                                className='py-2 px-2 form-control'
                                type="text"
                                placeholder="First Name"
                                name="firstName"
                                value={userData.firstName || ''}
                                onChange={handleChange}
                            />
                        </div>
                        <div className='py-1'>
                        <p className="pb-1 m-0 profile-text">Last name</p>
                            <input
                                className='py-2 px-2 form-control'
                                type="text"
                                placeholder="Last Name"
                                name="lastName"
                                value={userData.lastName || ''}
                                onChange={handleChange}
                            />
                        </div>
                        <div className='py-1'>
                            <p className="pb-1 m-0 profile-text">Email name</p>
                            <input
                                className='py-2 px-2 form-control'
                                type="text"
                                placeholder="Email"
                                name="email"
                                value={userData.email || ''}
                                onChange={handleChange}
                            />
                        </div>
                        <div className='py-1'>
                            <input
                                className='py-2 px-2 form-control'
                                type="password"
                                placeholder="Password"
                                name="password"
                                value={userData.password || ''}
                                onChange={handleChange}
                            />
                        </div>
                        <div className='py-1'>
                            <input
                                className='py-2 px-2 form-control'
                                type="text"
                                placeholder="Company Name"
                                name="companyName"
                                value={userData.companyName || ''}
                                onChange={handleChange}
                            />
                        </div>
                        <div className='py-1'>
                            <input
                                className='py-2 px-2 form-control'
                                type="text"
                                placeholder="Slogan (Optional)"
                                name="companySlogan"
                                value={userData.companySlogan || ''}
                                onChange={handleChange}
                            />
                        </div>
                        <div className="text-center pt-3">
                            <button type="submit" className="mx-1 add-item-btn my-1 p-2 px-3">Save Changes</button>
                        </div>
                    </form>
                </div>
            </div>
            <div className="col-12 col-sm-12 col-md-4 col-lg-4">
                <div className="inner-bg p-3 py-4">
                    <h4 className="h4">
                        Your Receipts
                    </h4>
                </div>
            </div>
        </div>
    )
}

export default Profile;
